<?php/*** @author Salahedine MOUDNI <moudni.salaheddine@gmail.com>* this class loads the  skin**/class Baty_View_Helper_LoadSkin extends Zend_View_Helper_Abstract{	public function loadSkin($skin = "defaultback"){		$skinData = new Zend_Config_Xml(APPLICATION_PATH.'/design/'.$skin.'/skin.xml');		$stylesheets = $skinData->stylesheets->stylesheet->toArray();		$scripts = $skinData->scripts->script->toArray();		if (is_array($scripts)){			foreach ($scripts as $script){				$this->view->headScript()->appendFile('../skins/'.$skin.'/js/'.$script);				}		}		if (is_array($stylesheets)){			foreach ($stylesheets as $stylesheet){				$this->view->headLink()->appendStylesheet('/skins/'.$skin.'/css/'.$stylesheet);				}		}	}}